import React, { Component } from 'react'
import { Switch, Route, withRouter } from 'react-router-dom';

import './App.css';
import InputName from './InputName';
import 'bulma/css/bulma.css'
import Chat from './Chat';


class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      userlist:["Pepe", "David", "Balta"],
      username: null
    }
  }

  newUser(e, user){
    e.preventDefault()

    const newList = [...this.state.userlist]
    newList.push(user)
    this.setState({
      ...this.state,
      userlist: newList,
      username: user
    })
    this.props.history.push("/chat")
  }

  render(){

    return (
      <div className="App">
        <Switch>
          <Route
            exact path="/"
            render={props => <InputName {...props} newUser={(user) => this.newUser(user)}/>}
            />
          <Route
            exact path="/chat"
            render={props => <Chat {...props} users={this.state.userlist} user={this.state.username}/>}
            />
        </Switch>
      </div>
    );

  }
}

export default withRouter(App)
