import React, { Component } from 'react'
import { Route, Redirect } from 'react-router'


import { Container } from 'bloomer';
import { Columns } from 'bloomer/lib/grid/Columns';
import { Column } from 'bloomer/lib/grid/Column';
import { Box } from 'bloomer/lib/elements/Box';
import { Content } from 'bloomer/lib/elements/Content';
import { Input } from 'bloomer/lib/elements/Form/Input';
import { Control } from 'bloomer/lib/elements/Form/Control';
import { Button } from 'bloomer/lib/elements/Button';
import { Field } from 'bloomer/lib/elements/Form/Field/Field';

export default class Chat extends Component {
  constructor(props){
    super(props)
    this.state = {
      messagesList: [
        {user: "pepe",
      message: "hola"}
      ],
      message: ""
    }
  }

  submitMessage(e){
    e.preventDefault()
    if(this.state.message.trim() !== ""){
      const messages = [...this.state.messagesList]
    messages.push({
      user: this.props.user,
      message: this.state.message
    })

    this.setState({
      ...this.state,
      messagesList: messages,
      message: ""
    })
    }


  }

  handleChange(e){
    console.log(e.target.value)
    this.setState({message: e.target.value})
  }

  render() {
    const {users, user} = this.props

    if(!user){
      <Redirect to="/"/>
    }

    return (
      <Container  className="chat-container is-fluid">
      <Content>
      <Columns isSize="1/4">
        <Column>
        {users.map(user => {
          return <p>{user}</p>
        })}
        </Column>
        <Column  isSize="3/4">
        <Box className="chat-area">
        {this.state.messagesList.map(message =>{
          return <p>{message.user}: {message.message} </p>
        })}
        </Box>
        <form className="text-form" onSubmit={(e) => this.submitMessage(e)}>
          <Field isGrouped>
          <Input
            onChange={e => this.handleChange(e)}
            type="text" 
            placeholder="write a message..."
            value={this.state.message}/>
          <Control>
            <Button 
              isColor="danger"
              onClick={(e) => this.submitMessage(e)}>
              Send
            </Button>
          </Control>

          </Field>
        </form>
        </Column>
      </Columns>

      </Content>
      </Container>
    )
  }
}
