import React, { Component } from 'react'
import { Hero,  HeroBody } from 'bloomer';
import { Field } from 'bloomer/lib/elements/Form/Field/Field';
import { Control } from 'bloomer/lib/elements/Form/Control';
import { Input } from 'bloomer/lib/elements/Form/Input';
import { Column } from 'bloomer/lib/grid/Column';
import { Columns } from 'bloomer/lib/grid/Columns';
import { Button } from 'bloomer/lib/elements/Button';

export default class InputName extends Component {
  constructor(props){
    super(props)
    this.state = {
      userName: ""
    }
  }

  handleChange(e){
    console.log(e.target.value)
    this.setState({userName: e.target.value})
  }
  render() {
    const {newUser} = this.props
    return (
      <Hero isColor='info' isSize='large' className="hero-section">
        <HeroBody>
            <Columns isCentered isVCentered>
            <Column isSize={{mobile: 8, default: 4}} isCentered>
            <Field>
              <Control>
                <Input 
                  onChange={e => this.handleChange(e)}
                  type="text" 
                  placeholder="your name..."
                  value={this.state.userName}/>
              </Control>
            </Field>
            <Button 
              isColor="danger" 
              onClick={()=> newUser(this.state.userName)}>Chat!</Button>
            </Column>
            </Columns>
        </HeroBody>
</Hero>
    )
  }
}
