import React, { Component } from 'react'
import { Container } from 'bloomer';
import { Columns } from 'bloomer/lib/grid/Columns';
import { Column } from 'bloomer/lib/grid/Column';
import { Box } from 'bloomer/lib/elements/Box';
import { Content } from 'bloomer/lib/elements/Content';
import { Input } from 'bloomer/lib/elements/Form/Input';
import { Control } from 'bloomer/lib/elements/Form/Control';
import { Button } from 'bloomer/lib/elements/Button';
import { Field } from 'bloomer/lib/elements/Form/Field/Field';

export default class Chat extends Component {
  constructor(props){
    super(props)
    this.state = {
      messagesList: [],
      message: ""
    }
  }

  submitMessage(e){
    e.preventDefault()
    const messages = [...this.state.messagesList]
    messages.push(this.state.message)

    this.setState({
      ...this.state,
      messagesList: messages,
      message: ""
    })

  }

  handleChange(e){
    console.log(e.target.value)
    this.setState({message: e.target.value})
  }

  render() {
    return (
      <Container  className="chat-container is-fluid">
      <Content>
      <Columns isSize="1/4">
        <Column>
        {this.props.users.map(user => {
          return <p>{user}</p>
        })}
        </Column>
        <Column  isSize="3/4">
        <Box className="chat-area">
        {this.state.messagesList.map(message =>{
          return <p>Pepe: {message} </p>
        })}
        </Box>
        <form className="text-form" onSubmit={() => this.submitMessage(e)}>
          <Field isGrouped>
          <Input
            onChange={e => this.handleChange(e)}
            type="text" 
            placeholder="write a message..."
            value={this.state.userName}/>
          <Control>
            <Button 
              isColor="danger"
              onClick={() => this.submitMessage(e)}>
              Send
            </Button>
          </Control>

          </Field>
        </form>
        </Column>
      </Columns>

      </Content>
      </Container>
    )
  }
}
