import React, { Component } from 'react'
import './App.css';
import InputName from './InputName';
import 'bulma/css/bulma.css'


export default class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      userlist:["Pepe", "David", "Balta"]
    }
  }

  newUser(user){
    const newList = [...this.state.userlist]
    console.log(`Added user: ${user}`)
    newList.push(user)
    this.setState({
      ...this.state,
      userlist: newList
    })
  }

  render(){
    return (
      <div className="App">

        <InputName newUser={(user) => this.newUser(user)}></InputName>
      </div>
    );

  }
}
